"use strict";angular.module("QuizApp",[]).controller("QuizCtrl",function(n,t){n.answered=!1;n.title="loading question...";n.options=[];n.correctAnswer=!1;n.working=!1;n.answer=function(){return n.correctAnswer?"correct":"incorrect"};n.nextQuestion=function(){n.working=!0;n.answered=!1;n.title="loading question...";n.options=[];t.get("/api/trivia").success(function(t){n.options=t.options;n.title=t.title;n.answered=!1;n.working=!1}).error(function(){n.title="Oops... something went wrong";n.working=!1})};n.sendAnswer=function(i){n.working=!0;n.answered=!0;t.post("/api/trivia",{questionId:i.questionId,optionId:i.id}).success(function(t){n.correctAnswer=t;n.working=!1}).error(function(){n.title="Oops... something went wrong";n.working=!1})}});